import React from 'react';
import PropTypes from 'prop-types';
import Checkbox from './newcheckbox';
const checkboxes = [
    {
        name: 'Delhi',
        key: 'Delhi',
        label: 'Delhi',
    },
    {
        name: 'Mumbai',
        key: 'Mumbai',
        label: 'Mumbai',
    },
    {
        name: 'Banglore',
        key: 'Banglore',
        label: 'Banglore',
    },
];

class CheckboxContainer extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            checkedItems: new Map(),
            locations: '',
        }

        this.handleChange = this.handleChange.bind(this);
    }

    handleChange(e) {
        const item = e.target.name;
        const isChecked = e.target.checked;
        this.setState(prevState => {
            console.log(prevState);

            let locations = prevState.locations;
        // if (prevState.checkedItems.size > 0) {
        //     for (const [key, value] of prevState.checkedItems.entries()) {
        //         locations = locations === '' ? key : `,${key}`;
        //     }
        // }
        console.log('----------- ')

        console.log('before ')
        console.log(locations);
        if (isChecked) {
            if (!locations.includes(item)) {
                locations += locations === '' ? item : `,${item}`;
            }
        } else {
            if (locations.includes(item)) {
                console.log('need to remove this')
            }
        }

        console.log('----------- ')

        console.log('after ')
        console.log(locations);
        return { checkedItems: prevState.checkedItems.set(item, isChecked), locations }
    });
}

render() {
    return (
        <React.Fragment>
            {
                checkboxes.map(item => (
                    <label key={item.key}>
                        {item.name}
                        <Checkbox name={item.name} checked={this.state.checkedItems.get(item.name)} onChange={this.handleChange} />
                    </label>
                ))
            }
        </React.Fragment>
    );
}
}

export default CheckboxContainer;
