import React from 'react';
import './table.scss';
import { columns } from './constants';
import {
    decrement,
    increment,
    incrementByAmount,
    incrementAsync,
    selectCount
  } from './counterSlice'

const Row = ({ id, name, phone, email, gender, locations,remove }) => (
    <div className="row" id={id}>

        <div>{name}</div>
        <div>{phone}</div>
        <div>{email}</div>
        <div>{gender}</div>
        <div>{locations}</div>
        <div className="remove">
            <a href="#" onClick={() => remove(id)}>X</a>
        </div>
        <div className="remove">
            <a href="#" onClick={() => remove(id)}>X</a>
        </div>
    </div>
);

function createData(id, randomSelection) {
    return { id, name: `Task 403 ${randomSelection}`, phone: '9000202161' + randomSelection, email: 'abcd@gamil.com', gender: 'male', locations: 'hyd,abcd,acd' };
}

function getData(num) {
    const rows = [];
    for (let i = 0; i < num; i += 1) {
        const randomSelection = Math.floor(Math.random() * 10);
        rows.push(createData(i, randomSelection));
    }
    return rows;
}

class Table extends React.Component {

    state = {
        data: getData(100),
    };

    compareBy = (key) => {
        return function (a, b) {
            if (a[key] < b[key]) return -1;
            if (a[key] > b[key]) return 1;
            return 0;
        };
    };

    sortBy = (key) => {
        let arrayCopy = [...this.state.data];
        arrayCopy.sort(this.compareBy(key));
        this.setState({ data: arrayCopy });
    };

    remove = (rowId) => {
        // Array.prototype.filter returns new array
        // so we aren't mutating state here
        const arrayCopy = this.state.data.filter((row) => row.id !== rowId);
        this.setState({ data: arrayCopy });
    };

    getHeader = (columns) => {
        const data = columns.map((column) => {
            return <div id={column.name}>{column.displayName}</div>
        })
        console.log(data);
        return data;
    }
    render() {
        const rows = this.state.data.map((rowData) => <Row remove={this.remove} {...rowData} />);

        return (
            <div className="table">
                <div className="header">
                    {this.getHeader(columns)}

                    <div className="remove"></div>
                    <div className="remove"></div>
                </div>
                <div className="body">
                    {rows}
                </div>
            </div>
        );

    }
}

export default Table;
