const getHeader = (columns) => {
    const data = columns.map((column) => {
        return <div id={column.name}>{column.displayName}</div>
    })
    return data;
}

const getRows = (rows, remove) => {
    return rows ? rows.map((rowData) => <Row remove={this.remove} {...rowData} />) : [];
}


const Table = (props) => {
    const users = useSelector(selectUsers);
    const dispatch = useDispatch();

    const remove = (rowId) => {
        const arrayCopy = users.filter((row) => row.id !== rowId);
        dispatch(removeUser(arrayCopy));
    };

    return (users ?
        <div className="table">
            <div className="header">
                {getHeader(columns)}

                <div className="remove"></div>
                <div className="remove"></div>
            </div>
            <div className="body">
                {getRows(users, remove)}
            </div>
        </div> : <h2> Loading... </h2>
    );

}
