import React from 'react';
import PropTypes from 'prop-types';
import Checkbox from './newcheckbox';
const checkboxes = [
    {
        name: 'Delhi',
        key: 'Delhi',
        label: 'Delhi',
    },
    {
        name: 'Mumbai',
        key: 'Mumbai',
        label: 'Mumbai',
    },
    {
        name: 'Banglore',
        key: 'Banglore',
        label: 'Banglore',
    },
];

class CheckboxContainer extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            checkedItems: new Map(),
            location: '',
        }

        // this.handleChange = this.handleChange.bind(this);
    }
     getUpdateLocation = ()=>{
        let locations = this.state.location;

        if (isChecked) {
            if (!locations.includes(item)) {
                locations += locations === '' ? item : `,${item}`;
            }
        } else {
            if (locations.includes(item)) {
                let newLocations = '';
                if (this.state.checkedItems.size > 0) {
                    for (const [key, value] of this.state.checkedItems.entries()) {
                        if (value && newLocations === '' && key !== item) {
                            newLocations += key;
                        } else if (value) {
                            newLocations += key === item ? '' : `,${key}`;

                        }
                    }
                }
                locations = newLocations;
            }
        }
        return locations
     }
    handleChange = (e) => {
        const item = e.target.name;
        const isChecked = e.target.checked;
        console.log(this.state);
        const locations = getUpdateLocation();


        this.setState(prevState => {
            return { checkedItems: prevState.checkedItems.set(item, isChecked), location: locations }
        });
        console.log(this.state);
        console.log(locations);
    }

    render() {
        return (
            <React.Fragment>
                {
                    checkboxes.map(item => (
                        <label key={item.key}>
                            {item.name}
                            <Checkbox name={item.name} checked={this.state.checkedItems.get(item.name)} onChange={this.handleChange} />
                        </label>
                    ))
                }
            </React.Fragment>
        );
    }
}

export default CheckboxContainer;
