import React from 'react';
import UserDetails from './UserDetails';

const nameValidation = (fieldName, fieldValue) => {
    if (fieldValue.trim() === '') {
        return `${fieldName} is required`;
    }
    if (/[^a-zA-Z -]/.test(fieldValue)) {
        return 'Invalid characters';
    }
    if (fieldValue.trim().length < 3) {
        return `${fieldName} needs to be at least three characters`;
    }
    return null;
};

const emailValidation = email => {
    if (
        /^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/.test(
            email,
        )
    ) {
        return null;
    }
    if (email.trim() === '') {
        return 'Email is required';
    }
    return 'Please enter a valid email';
};

const ageValidation = age => {
    if (!age) {
        return 'Age is required';
    }
    if (age < 18) {
        return 'Age must be at least 18';
    }
    if (age > 99) {
        return 'Age must be under 99';
    }
    return null;
};
const genderValidation = gender => {
    if (!gender || gender === '' || gender === 'Select') {
        return 'Gender is required';
    }
    return null;
};
const locationValidation = locations => {
    if (!locations || locations === '') {
        return 'Location is required';
    }
    return null;
};

const validate = {
    name: name => nameValidation('Name', name),
    email: emailValidation,
    age: ageValidation,
    gender: genderValidation,
    locations: locationValidation,

};

const initialValues = {
    age: 0,
    email: '',
    name: '',
    gender: '',
    locations: 'hyd'
};

export default function UserForm() {
    return <>
        <h2>Add User</h2>

        <UserDetails validate={validate} initialValues={initialValues} />
    </>

}
